mapper.select:
SELECT SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, DEL, READCOUNT, FILENAME, OLDFILENAME FROM ( SELECT ROW_NUMBER()OVER(ORDER BY REF DESC, STEP ASC)AS RNUM, SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, DEL, READCOUNT, FILENAME, OLDFILENAME FROM CC_INFO WHERE 1=1
AND TITLE LIKE '%'||#{searchWord}||'%'
AND CONTENT LIKE '%'||#{searchWord}||'%'
AND ID=#{searchWord}
) A WHERE RNUM BETWEEN ${start} AND ${end}

SELECT SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, DEL, READCOUNT, FILENAME, OLDFILENAME
FROM CC_INFO 
WHERE SEQ=#{seq}

mapper.update:
UPDATE CC_INFO 
SET READCOUNT=READCOUNT+1
WHERE SEQ=#{seq}

UPDATE CC_INFO
SET DEL=1
WHERE SEQ=#{seq}

UPDATE CC_INFO SET
TITLE=#{title}, CONTENT=#{content}, WDATE=SYSDATE
WHERE SEQ=#{seq}

mapper.select:
SELECT NVL(COUNT(*), 0) AS CNT
FROM CC_INFO_RE
WHERE SEQ=#{seq}

SELECT SEQ, INFOSEQ, ID, CONTENT, WDATE, DEL
FROM CC_INFO_RE
WHERE INFOSEQ=#{seq}
